	select @Subject = case @xtype	when 'TR' then 'Trigger'
									when 'P' then 'Proc'
									when 'FN' then 'Func'
									when 'V' then 'View' end

	----strip out leading charaters
	--select @Routine = replace(@Routine, substring(@routine, 1, charindex('create', @Routine)-1), '')
