	begin
		select @Routine = replace(@Routine, 'create' + replicate(' ', @x) + @subject, 'create ' + @subject)
		select @Routine = replace(@Routine, 'create' + replicate(char(9), @x) + @subject,  'create ' + @subject)
		select @x = @x + 1	
	end
		
	return @Routine
	--select @Routine
