				select @Street=case when @sync_address_type = ''S1'' then c.SHIP_ADDRESS_1 when @sync_address_type = ''S2'' then c.SHIP_ADDRESS_2
					when @sync_address_type = ''S3'' then c.SHIP_ADDRESS_3 else c.SHIP_ADDRESS_1 end,
