		CountVariance int,
		PriceVariance decimal(19,4)
	)
	
	declare @cs datetime, @ce datetime, @ps datetime, @pe datetime, @tr_db varchar(50), @nsql nvarchar(max), @currentPeriod int,
		@StartOfFiscalYear datetime, @CompanyID int 
	
