	begin -- need to call again
	 select @TimeRemaining = @TimeRemaining - @Minutes

	 select @Count = count(*)
	 from #WeeklySchedule
	 where DayOfWeek = @DayOfWeek
	 and Working = 1
	 and Seq = @CurrentSequence + 1
	 if @Count = 0
