Begin
	Update #Leads
	Set CountryCode = 'CDN'
End
Else
Begin
	Select @Sql = 'Update #Leads
				   Set CountryCode = (Select Case When (CO_Country = ''U'') Then ''USA'' Else ''CDN'' END 
				   From ' + @TRDB + '..Company)'
	Exec (@Sql)
