begin
	declare 
	@MSP_ProjID int,
	@NewMSP_ProjID int,
	@MSP_Proj_Name varchar(255),
	@Date datetime,
	@Notes varchar(4000)

	---=================================================================================================
	------------------------------------Copy HB_ScheduleMap---------------------------------------------
	---=================================================================================================
	if @CleanOut = 'T'
	begin
		delete from hb_schedulemap where model_uid=@Dest_ID
	end

	if exists (select * from tempdb..sysobjects where id = object_id(N'tempdb..#tmp_schedulemap'))  
