	select @ExpC=ABS(@ExpC)
	select @ExpP=ABS(@ExpP)
	select @NSC=ABS(@NSC)
	select @NSP=ABS(@NSP)

	select @Current = (@NSC - @ExpC) 

	select @Previous = (@NSP - @ExpP) 
	

