				declare cur cursor for
				select p.po_detail_id, s.id from '+@tr_db+'..so_tasks s
				join '+@tr_db+'..po_detail p on p.so_task_id=s.id
				where s.REQUEST_REF_ID='+cast(@REQUEST_REF_ID as varchar)+'

