				fetch cur into @PO_DETAIL_ID, @SO_TASK_ID
				while @@fetch_status=0
				begin

					select @REV_CNT=count(*) from '+@tr_db+'..PO_DETAIL_REVISIONS where po_detail_id=@PO_DETAIL_ID
					if( @REV_CNT = 0 )
					begin
						set @REVISION_NUMBER = 0
					end	
					
