						begin
							
							declare @TASK_QTY money, @TASK_UNIT_PRICE money, @TASK_CONV_QTY money, @TASK_CONV_UNIT_PRICE money

							select @TASK_QTY=isnull(QTY_CUST,0), @TASK_CONV_QTY=isnull(QTY,0), @TASK_CONV_UNIT_PRICE=isnull(base_cost,0),
