							p.MISC_EST2=s.MISC_EST2, p.MISC_AMT2=s.MISC_AMT2, p.MISC_TYPE2=s.MISC_TYPE2
							from '+@tr_db+'..po_header p 
							left outer join '+@tr_db+'..so_landing_factors s on s.po_id=p.po_id
							where p.po_id=@PO_ID
							
						end
					end

					fetch cur into @PO_DETAIL_ID, @SO_TASK_ID
				end

