					 	If ((Select Count(ContactID) From Access Where ContactID=@ContactID AND UserLevel = 'V') = 0)
							Begin
								Insert into Access (ContactID, UserLevel) Values (@ContactID, 'V')
								If (@@Error <> 0)
									Begin
										RAISERROR('Error Updating Access Level User', 16, 1)
										ROLLBACK
									End
							End							
						
					End
				Else
					Begin
						If((Select Count(ContactID) From Access Where ContactID=@ContactID AND UserLevel ='V') > 0)
