CREATE procedure [dbo].[Tools_ReorderSeq] 
	@id int, 
	@new_seq int,
	@Table nvarchar(max),
	@idCol nvarchar(max),
	@SeqCol nvarchar(max),
    @WhereClause nvarchar(max)
