		when isnull(lv2_active_gc,''F'') = ''T'' then ''lv2Id'' when isnull(lv1_active_gc,''F'') = ''T'' then ''lv1Id'' else '''' end
	from '+@tr_db+'.dbo.[proj_cntl] '
