		if( @PrevMatchId = -1 )
		begin
			-- insert
			select @nSql = '
			declare @FileRepository_ID int, @IDValue int

			if( @TableDotField = ''FieldPO.Id'')
			begin
				select @TableDotField = ''PO_HEADER.PO_ID''
				select @IDValue=PO_ID from '+@tr_db+'.dbo.PO_HEADER where MatchId=@LinkMatchId
