		 @GoodUntilDate2, cast(@FinalPrice2 as money), cast(@GST2 as money), 
		 cast(@GSTRebate2 as money), cast(@GSTTotal2 as money)

select @s = @s + 'tr_UpdateSCQuote '
exec sp_logit @S


exec sp_logit 'tr_UpdateSCQuote Finished job'

  end 

 end
 
end









